import './App.css';
import { useEffect, useState } from 'react';
import Axios from 'axios';
import Table from 'react-bootstrap/Table';
import Form from 'react-bootstrap/Form';
import Button from 'react-bootstrap/Button';
import ButtonGroup from 'react-bootstrap/ButtonGroup';

function App() {
  const [data, setData] = useState([]);
  const [name, setName] = useState('');
  const [age, setAge] = useState(1);

  const getData = () => {
    Axios({
      method: 'get',
      url: 'http://localhost:7777/employee',
    })
      .then(function (response) {
        setData(response.data.data)
      });
  }

  const handleAdd = (e) => {
    e.preventDefault()
    Axios({
      method: 'post',
      url: 'http://localhost:7777/employee',
      data: {
        name: name,
        age: parseInt(age)
      }
    })
      .then(function (response) {
        setName('')
        setAge(1)
        getData()
      });
  }

  useEffect(() => {
    getData()
  }, []);

  return (
    <>
      <Form onSubmit={handleAdd}>
        <Form.Group className="mb-3" controlId="formBasicName">
          <Form.Label>Name</Form.Label>
          <Form.Control value={name} name="name" type="text" onChange={(e) => setName(e.target.value)} placeholder="Enter name" />
        </Form.Group>

        <Form.Group className="mb-3" controlId="formBasicAge">
          <Form.Label>Age</Form.Label>
          <Form.Control value={age} name="age" type="number" onChange={(e) => setAge(e.target.value)} placeholder="Enter age" />
        </Form.Group>
        <Button variant="primary" type="submit">
          Submit
        </Button>
      </Form>
      <Table striped bordered hover variant="dark">
        <thead>
          <tr>
            <th>#</th>
            <th>Name</th>
            <th>Age</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item, index) => {
            return <tr key={index}>
              <td>{index + 1}</td>
              <td>{item.name}</td>
              <td>{item.age}</td>
              <td><ButtonGroup aria-label="Action">
                <Button size="sm" variant="primary">Edit</Button>
                <Button size="sm" variant="danger">Delete</Button>
              </ButtonGroup></td>
            </tr>
          })}
        </tbody>
      </Table>
    </>
  );
}

export default App;
